#!/bin/bash`n`necho "Vercel Deployment Script"`necho "========================"`n`n# Initialize Git repository if not already initialized`nif [ ! -d .git ]; then`n    echo "Initializing Git repository..."`n    git init`n    git add .`n    git commit -m "Initial commit for deployment"`nfi`n`n# Run local build test`necho "Running local build test..."`nnpm run build`n`nif [ $? -eq 0 ]; then`n    echo "Build successful! Deploying to Vercel..."`n    vercel --prod`nelse`n    echo "Build failed! Please check the errors."`n    exit 1`nfi
